unitsize(0.8 cm);

real func1 (real x) {
  return(sin(x/3 + pi/9));
}

real func2 (real x) {
  return(2*sin(x));
}

draw(graph(func1,0,6*pi),red);
draw(graph(func2,0,6*pi),blue);
